import React, {useState, useEffect} from 'react'
import {withFormik, Field, Form as FormikForm} from 'formik'
import * as Yup from 'yup'

import 'antd/dist/antd.css';
import { Form, Icon, Input, Button, Checkbox } from 'antd';


const LogInForm = ({touched, errors, status}, props) =>{
    useEffect(() => {
        if(status){
        console.log(status)
        }
    }, [status])
    return(
        <FormikForm>
            <FormItem>
                {touched.username && errors.username && <p className="error_message">{errors.username}</p>}
                <Field type="text" name="username" placeholder="username" />
            </FormItem>
             
            {touched.password && errors.password && <p className="error_message">{errors.password}</p>}
            <Field type="password" name="password" placeholder="password"/>
            <Button type="normal">LogIn</Button>
        </FormikForm>
    )
}

const LogIn = withFormik({
    mapPropsToValues({username, password}){
       return { 
        username: username || "", 
        password: password || ""}
    }, 
    validationSchema:Yup.object().shape({
        username: Yup.string()
        .required('please enter your username'), 

        password: Yup.string()
        .required('please enter your password')

    }), 
    handleSubmit(values, {resetForm, setStatus}){
        setStatus(values)
        resetForm()
    }
})(LogInForm)

export default LogIn
