const SignUp = withFormik({
  mapPropsToValues({
    firstname,
    lastname,
    email,
    username,
    password,
    age,
    gender,
    feet,
    inches,
    weight,
    activity,
    goals
  }) {
    return {
      firstname: firstname || "",
      lastname: lastname || "",
      email: email || "",
      username: username || "",
      password: password || "",
      age: age || 25,
      gender: gender || "male",
      feet: feet || 5,
      inches: inches || 9,
      weight: weight || 180,
      activity: activity || 0,
      goals: goals || -10
    };
  },

  validationSchema: Yup.object().shape({
    firstname: Yup.string().required('Please enter your first name'),
    lastname: Yup.string().required('Please enter your last name'),
    email: Yup.string().email('Please enter a valid email ').required('An email is required to create an account'),
    username: Yup.string().required('An username required to create an account'),
    password: Yup.string().required('Please enter a password'),
    age: Yup.number().required('Age is required'),
    gender: Yup.string().required('Please select a gender'),
    feet: Yup.number().required('Please enter you feet'),
    inches: Yup.number().required('Please enter your feet'),
    weight: Yup.number().required('Please enter your weight'),
    activity: Yup.number().required('Please select an acitivty level'),
    goals: Yup.number().required('Please select a goal')
  }),

  handleSubmit(values, {
    resetForm,
    setStatus
  }) {
    axios.post('https://reqres.in/api/users/', values).then(response => {
      console.log(response);
    }).catch(error => console.log(error.response));
    resetForm();
  }

