import React, {useState, useEffect} from 'react'
import {withFormik, Field, Form as FormikForm, Field as FormikField} from 'formik'
import * as Yup from 'yup'

import 'antd/dist/antd.css';
import { Form, Icon, Input, Button, Checkbox } from 'antd';

const FormItem = Form.Item

const LogInForm = ({touched, errors, status}, props) =>{
   /* useEffect(() => {
        if(status){
        console.log(status)
        }
    }, [status])*/
    return(
        <FormikForm>
            <Form>
                <FormItem>
                    {touched.username && errors.username && <p className="error_message">{errors.username}</p>}
                    
                    <FormikField>
                    {getFieldDecorator('username', {
                        rules: [{ required: true, message: 'Please input your username!' }],
                    })(
                        <Input
                            prefix={<Icon type="user" style={{ color: 'rgba(0,0,0,.25)' }} />}
                            name="username" placeholder="username"
                        />,
                    )}
                    </FormikField>
                 </FormItem>

                <FormItem>  
                {touched.password && errors.password && <p className="error_message">{errors.password}</p>}
                <FormikField type="password" name="password" placeholder="password"/>
                </FormItem>
            <Button type="primary" htmlType="submit">LogIn</Button>
            </Form>
        </FormikForm>
    )
}

const LogIn = withFormik({
    mapPropsToValues({username, password}){
       return { 
        username: username || "", 
        password: password || ""}
    }, 
    validationSchema:Yup.object().shape({
        username: Yup.string()
        .required('please enter your username'), 

        password: Yup.string()
        .required('please enter your password')

    }), 
    handleSubmit(values, {resetForm, setStatus}){
        //setStatus(values)
        console.log(values)
        resetForm()
    }
})(LogInForm)

export default LogIn
