import React, {useState} from 'react'
import {Link} from 'react-router-dom'
import SignUp from './SignUp'
import LogIn from './LogIn'

import 'antd/dist/antd.css';
import {Button, Card} from 'antd'

const SideInformation = props => {
    return (
    <div class="sidePanel">
        <p className="quote">{props.quote}</p> 
        <p className="author">{props.author}</p>
        <p className="message">{props.message}</p>

        <Button onClick={ props.click}>{props.button}</Button>
    </div>
    )
}
const FormContainer = props => {
    const [returningUser, setReturningUser] = useState(true)
    const switchForm = () => setReturningUser(!returningUser)
    return(
        <Card style={{margin: 'auto', width: '80%', display: 'flex'}}>
            <div className='container'>
                <div class="otherForm">
                    {
                        (returningUser) ? <SideInformation quote='Setting goals is the first step in turning the invisible into the visible.'  author="Tony Robbins" message="Don't have an account yet?" click={() => switchForm()} button="SignUp"/> : <SideInformation quote="Motivation is what gets you started. Habit is what keeps you going." author="Jim Ryun" message="Already have an account?" click={() => switchForm()} button="Login"/>
                    }
                </div>
                
                <div class="currentForm">
                    {
                        (returningUser) ? <LogIn /> : <SignUp />
                    }
                </div>

            </div>

        </Card>
    )
}
export default FormContainer
